def on_event(self, event):
logging.debug(f&quot;Event received: {event.event}&quot;)
if event.event == &#39;thread.run.requires_action&#39;:
run_id = event.data.id # 从事件数据中检索运行 ID
logging.debug(f&quot;Handling takes action for run_id: {run_id}&quot;)
self.handle_requires_action(event.data, run_id)
elif event.event == &#39;thread.run.text_delta&#39;:
self.handle_text_delta(event.data)
elif event.event == &#39;thread.run.completed&#39;:
self.response_complete.set()
self.handle_response_complete()
elif event.event == &#39;thread.run.failed&#39;:
login.error(f&quot;运行失败，数据：{event.data}&quot;)

def handle_requires_action(self, data, run_id):
login.debug(f&quot;需要操作数据：{data}&quot;)
tool_outputs = []
for tool in data.required_action.submit_tool_outputs.tool_calls:
function_name = tool.function.name
login.debug(f&quot;调用函数：{function_name}，参数：{tool.function.arguments}&quot;)
if function_name in self.function_handlers:
handler = self.function_handlers[function_name]
try:
tool_output = handler(tool.function.arguments, tool.id)
logs.debug(f&quot;工具输出：{tool_output}&quot;)
tool_outputs.append(tool_output)
except Exception as e:
logs.error(f&quot;错误处理函数 {function_name}：{e}&quot;)
tool_outputs.append({
&quot;tool_call_id&quot;: tool.id,
&quot;output&quot;: f&quot;错误：{str(e)}&quot;
})
else:
logs.error(f&quot;不支持的函数：{function_name}&quot;)
tool_outputs.append({
&quot;tool_call_id&quot;: tool.id,
&quot;output&quot;: f&quot;不支持函数 {function_name}。&quot;
})

# 同时提交所有 tool_outputs
logs.debug(f&quot;提交工具输出：{tool_outputs}&quot;)
self.submit_tool_outputs(tool_outputs, run_id)
logging.debug(&quot;已提交工具输出，检查下一步操作。&quot;)

def submit_tool_outputs(self, tool_outputs, run_id):
logging.debug(f&quot;提交 run_id 的工具输出：{run_id}&quot;)
with self.client.beta.threads.runs.submit_tool_outputs_stream(
thread_id=self.thread_id,
run_id=run_id,
tool_outputs=tool_outputs,
event_handler=self,
) as stream:
for text in stream.text_deltas:
logging.debug(f&quot;流文本增量：{text}&quot;)
print(text, end=&quot;&quot;, flush=True)
with self.text_lock:
self.accumulated_text += text # 将文本视为字符串
logging.debug(&quot;已完成流式传输工具输出。&quot;)
logging.debug(f&quot;累计文本： {self.accumulated_text}&quot;)
print()
