def generate_survey(text, middle_file_name):
max_chunk_size = 250 
text_length = len(text)
text_chunks = []

for i in range(0, text_length, max_chunk_size):
text_chunks.append(text[i:i + max_chunk_size])

combined_response = []

chat_history = [{&quot;role&quot;: &quot;system&quot;, &quot;content&quot;: &quot;您是调查生成器。&quot;}]
for index, chunk in enumerate(text_chunks):
chat_history.append({
&quot;role&quot;: &quot;user&quot;,
&quot;content&quot;: f&quot;&quot;&quot;

