// ==用户脚本==
// @name ChatGPT.com 移动布局 Enter 键修复
// @命名空间 https://chatgpt.com
// @版本2
// @description 启用移动布局上的 Enter 键提交
// @author rbutera
// @match https://chatgpt.com/*
// @授予无
// ==/用户脚本==


// 这是 Lodimas 的工作分叉 - 适合 chatgpt.com
（功能（） {
    &#39;使用严格&#39;；

    // 通过 ID 访问文本区域
    var textarea = document.getElementById(&#39;提示文本区域&#39;);

    // 通过按钮的 data-testid 属性访问按钮
    var sendbutton = document.querySelector(&#39;button[data-testid=&quot;fruitjuice-send-button&quot;]&#39;);

    // 处理按键事件的函数
    函数handleKeyPress（事件）{
        //检查是否按下了Enter键
        if (event.keyCode === 13) {
            event.preventDefault();
            // 从 DOM 重新捕获按钮
            sendbutton = document.querySelector(&#39;button[data-testid=&quot;fruitjuice-send-button&quot;]&#39;);
            if (sendbutton &amp;&amp; !event.shiftKey) {
                发送按钮.click();
            }
        }
    }

    // 将事件监听器添加到文本区域
    如果（文本区域）{
        textarea.addEventListener(&#39;按键&#39;,handleKeyPress);
    }
})();
