@bot.message_handler(content_types=[&#39;document&#39;])
def handle_document(message):
try:
# 获取文档的文件路径
file_info = bot.get_file(message.document.file_id)

file_response = request.get(&#39;https://api.telegram.org/file/bot{0}/{1}&#39;.format(TELEBOT_API_TOKEN, file_info.file_path))

file_extension = Path(file_info.file_path).suffix.lower() # 使用 pathlib.Path 实现更好的跨平台兼容性

if file_extension in [&#39;.pdf&#39;, &#39;.docx&#39;]:

json_response = extract_data_from_file(file_response)
print(json_response) # 调试目的
bot.reply_to(message, json_response)

else:
bot.reply_to(message, &quot;不支持的文档类型。请以 PDF 或 Word 文档格式发送您的简历。&quot;)
except Exception as e:
print(f&quot;发生错误：{e}&quot;)
bot.reply_to(message, &quot;无法处理您的文档。请重试。&quot;)

def extract_data_from_file(file_response):
try:

# 将文件内容读入字节
file_bytes = file_response.content

# 将用户提供的文件上传到 OpenAI
message_file = client.files.create(
file=file_bytes, purpose=&quot;assistants&quot;
)

# 将文件添加到向量存储
file_batch = client.beta.vector_stores.file_batches.upload_and_poll(
vector_store_id=vector_store.id, files=[message_file]
)

# 确保文件已编入索引
if file_batch.status != &#39;completed&#39;:
return &quot;无法在向量中索引简历store。&quot;

# 更新助手以使用向量存储
assistant = client.beta.assistants.update(
assistant_id=assistant.id,
tool_resources={&quot;file_search&quot;: {&quot;vector_store_ids&quot;: [vector_store.id]}},
)

# 创建线程并将文件附加到消息
thread = client.beta.threads.create(
messages=[
{
&quot;role&quot;: &quot;user&quot;,
&quot;content&quot;: &quot;请将我的简历转换为 JSON 格式。&quot;,
&quot;attachments&quot;: [
{&quot;file_id&quot;: message_file.id, &quot;tools&quot;: [{&quot;type&quot;: &quot;file_search&quot;}]}
],
}
]
)

# 轮询线程运行
run = client.beta.threads.runs.create_and_poll(
thread_id=thread.id, assistant_id=assistant.id
)

# 从运行中检索消息
messages = list(client.beta.threads.messages.list(thread_id=thread.id, run_id=run.id))
json_response = messages[0].content[0].text
return json_response

except Exception as e:
print(f&quot;文件处理过程中发生错误：{e}&quot;)
return &quot;无法处理文件。&quot;
