public void HandleInput()
{

    字符串 userInput = inputField.text;
    StartCoroutine(SendToGPT(userInput));

}

私有IEnumerator SendToGPT（字符串输入）
{
字符串 apiUrl = “url”;
string requestBody = &quot;{\&quot;prompt\&quot;: \&quot;&quot; + input + &quot;\&quot;, \&quot;max_tokens\&quot;: 500}&quot;;

UnityWebRequest 请求 = UnityWebRequest.PostWwwForm(apiUrl, requestBody);

request.SetRequestHeader(&quot;Content-Type&quot;, &quot;application/json&quot;);
request.SetRequestHeader(&quot;授权&quot;, &quot;承载 myapi&quot;);

产量返回请求.SendWebRequest();

if (request.result == UnityWebRequest.Result.Success)
{
    字符串responseJson = request.downloadHandler.text;

    字符串 generatedText = ParseResponseFromJSON(responseJson);

    产量返回生成的文本；
}
别的
{
    Debug.LogError(&quot;发送请求时出错：&quot; + request.error);
    产量返回空；
}

}
