GptResponse 响应 = JsonUtility.FromJson(jsonResponse);
 if (response.choices != null &amp;&amp; response.choices.Length &gt; 0)
    {
        字符串 generatedText = response.choices[0].text;
        返回生成的文本；
    }
    别的
    {
        Debug.LogError(&quot;在 JSON 响应中找不到选择。&quot;);
