async function handleStream(reader:ReadableStreamDefaultReader&lt;Uint8Array&gt;, decrypt:TextDecoder) {

while (true){
const {done, value} = await reader.read();
if (done) break;
const chunk = 解码器.decode(value, {stream: true})

const eventData = chunk
.split(&quot;\n\n&quot;)
.filter((line) =&gt; line.startsWith(&quot;event: &quot;))
.map((line) =&gt; line.replace(/^event:/, &quot;&quot;))

eventData.forEach(data =&gt; {
try {

const parsedData = JSON.parse(data);

if (parsedData.type === &quot;callProgress&quot;) {
setProgress(
parsedData.output[parsedData.output.length - 1].content
);
setCurrentTols(parsedData.tool?.description || &quot;&quot;);
} else if ( parsedData.type === &#39;callStarted&#39;){
setCurrentTols(parsedData.tool?.description || &quot;&quot;);
} else if ( parsedData.type === &#39;runFinish&#39;){
setRunFinished(true);
setRunStarted(false);
} else {
setEvents((prevEvent) =&gt; [...prevEvent, parsedData]);
}

} catch (error) {
console.log(&quot;error bang&quot;, error)
}
})
}
}

